
public class Kona extends Car {

	public Kona() {
		// TODO Auto-generated constructor stub
	}

	@Override
	public String toString() {
		return "Kona [name=" + name + ", steering=" + steering + ", gear=" + gear + ", speed=" + speed + ", getName()="
				+ getName() + ", toString()=" + super.toString() + ", getSteering()=" + getSteering() + ", getGear()="
				+ getGear() + ", getSpeed()=" + getSpeed() + ", getClass()=" + getClass() + ", hashCode()=" + hashCode()
				+ "]";
	}
	
	

}
